import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Auth.css';

function UserMenu({ user, onLogout, onShowAuth, onShowOrders, cartItemCount = 0 }) {
  const [showDropdown, setShowDropdown] = useState(false);
  const [wishlistCount, setWishlistCount] = useState(0);
  const [cartCount, setCartCount] = useState(0);
  const navigate = useNavigate();

  // L·∫•y s·ªë l∆∞·ª£ng wishlist t·ª´ localStorage
  useEffect(() => {
    const updateWishlistCount = () => {
      const wishlist = JSON.parse(localStorage.getItem('wishlist')) || [];
      const cart = JSON.parse(localStorage.getItem('cart')) || [];
      setWishlistCount(wishlist.length);
      setCartCount(cart.reduce((total, item) => total + item.quantity, 0));
    };

    updateWishlistCount();

    // L·∫Øng nghe s·ª± ki·ªán wishlist thay ƒë·ªïi
    window.addEventListener('wishlistUpdated', updateWishlistCount);
    window.addEventListener('cartUpdated', updateWishlistCount);

    return () => {
      window.removeEventListener('wishlistUpdated', updateWishlistCount);
      window.removeEventListener('cartUpdated', updateWishlistCount);
    };
  }, []);

  const getInitials = (name) => {
    if (!name) return 'U';
    return name
      .split(' ')
      .map(word => word[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    onLogout();
    setShowDropdown(false);
    navigate('/');
  };

  const handleOrdersClick = () => {
    navigate('/orders');
    setShowDropdown(false);
  };

  const handleWishlistClick = () => {
    navigate('/wishlist');
    setShowDropdown(false);
  };

  const handleProfileClick = () => {
    navigate('/profile');
    setShowDropdown(false);
  };

  const handleCartClick = () => {
    navigate('/cart');
    setShowDropdown(false);
  };

  const handleAddressClick = () => {
    navigate('/addresses');
    setShowDropdown(false);
  };

  const handleChangePasswordClick = () => {
    navigate('/change-password');
    setShowDropdown(false);
  };

  if (!user) {
    return (
      <button 
        className="auth-btn " 
        onClick={onShowAuth}
        style={{ 
          padding: '8px 16px', 
          fontSize: '14px',
          background: '#3498db',
          color: 'white'
        }}
      >
        ƒêƒÉng nh·∫≠p
      </button>
    );
  }

  const orderCount = user.orderCount || 0;


  return (
    <div className="user-menu" onMouseEnter={() => setShowDropdown(true)} onMouseLeave={() => setShowDropdown(false)}>
      onClick={(e) => {
        e.stopPropagation();
        if (!showDropdown) setShowDropdown(true);
      }}
      <div className="user-avatar">
        {getInitials(user.fullName)}
        {(wishlistCount > 0 || cartCount > 0) && (
          <span className="notification-dot"></span>
        )}
      </div>
      <span className="user-name">Xin ch√†o, {user.fullName.split(' ')[0]}</span>

      {showDropdown && (
        <div className="user-dropdown">
          <div className="dropdown-header">
            <div className="user-avatar large">{getInitials(user.fullName)}</div>
            <div className="user-dropdown-info">
              <div className="dropdown-username">{user.fullName}</div>
              <div className="dropdown-useremail">{user.email}</div>
              <div className="user-stats">
                <div className="stat-item">
                  <span className="stat-number">{orderCount}</span>
                  <span className="stat-label">ƒê∆°n h√†ng</span>
                </div>
                <div className="stat-item">
                  <span className="stat-number">{cartCount}</span>
                  <span className="stat-label">Gi·ªè h√†ng</span>
                </div>
                <div className="stat-item">
                  <span className="stat-number">{wishlistCount}</span>
                  <span className="stat-label">Y√™u th√≠ch</span>
                </div>
              </div>
            </div>
        </div>
         <button className="dropdown-item" onClick={handleProfileClick}>
            üë§ Th√¥ng tin t√†i kho·∫£n
          </button>

          <button className="dropdown-item" onClick={handleOrdersClick}>
            üì¶ ƒê∆°n h√†ng c·ªßa t√¥i
            {orderCount > 0 && <span className="badge">{orderCount}</span>}
          </button>

          <button className="dropdown-item" onClick={handleWishlistClick}>
            ‚ù§Ô∏è Danh s√°ch y√™u th√≠ch
            {wishlistCount > 0 && <span className="badge">{wishlistCount}</span>}
          </button>

          <button className="dropdown-item" onClick={handleCartClick}>
            üõí Gi·ªè h√†ng
            {cartCount > 0 && <span className="badge">{cartCount}</span>}
          </button>

          <button className="dropdown-item" onClick={handleAddressClick}>
            üìç S·ªï ƒë·ªãa ch·ªâ
          </button>

          <button className="dropdown-item" onClick={handleChangePasswordClick}>
            üîí ƒê·ªïi m·∫≠t kh·∫©u
          </button>

          {user.role === 'admin' && (
            <button className="dropdown-item admin-item" onClick={() => navigate('/admin')}>
              ‚öôÔ∏è Qu·∫£n tr·ªã vi√™n
            </button>
          )}

          <div className="dropdown-divider"></div>

          <button className="dropdown-item logout" onClick={handleLogout}>
            üö™ ƒêƒÉng xu·∫•t
            <span className="role-tag">{user.role === 'admin' ? 'Qu·∫£n tr·ªã vi√™n' : 'Th√†nh vi√™n'}</span>
          </button>
        </div>
      )}
    </div>
  );
}

export default UserMenu;
