import { GoogleLogin } from '@react-oauth/google';
import './Auth.css';

const USE_BACKEND = false; // ƒê·∫∑t true khi c√≥ backend
const BACKEND_URL = 'http://localhost:5000/api';

function Auth({ onClose, onLogin, onRegister }) {
  const GOOGLE_CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID || '';

  console.log('=== AUTH COMPONENT RENDER ===');
  console.log('GOOGLE_CLIENT_ID:', GOOGLE_CLIENT_ID);
  console.log('Client ID length:', GOOGLE_CLIENT_ID.length);
  console.log('Should show button?', !!GOOGLE_CLIENT_ID && GOOGLE_CLIENT_ID.length > 10);

  const [activeTab, setActiveTab] = useState('login');
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({ 
    fullName: '', 
    email: '', 
    password: '', 
    confirmPassword: '' 
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  const handleGoogleSuccess = async (credentialResponse) => {
    try {
      setIsLoading(true);
      setErrors({});

      console.log('üîê ƒêang ƒëƒÉng nh·∫≠p v·ªõi Google...');

      // Decode Google token ƒë·ªÉ l·∫•y th√¥ng tin
      const token = credentialResponse.credential;
      const base64Url = token.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));

      const payload = JSON.parse(jsonPayload);
      const { sub: googleId, email, name: fullName, picture: avatar } = payload;

      // L·∫•y users t·ª´ localStorage
      const users = JSON.parse(localStorage.getItem('users') || '[]');

      // T√¨m user hi·ªán c√≥
      let user = users.find(u => u.email === email);

      if (!user) {
        // T·∫°o user m·ªõi t·ª´ Google
        user = {
          id: Date.now(),
          googleId,
          email,
          fullName,
          avatar,
          isGoogleUser: true,
          emailVerified: true,
          createdAt: new Date().toISOString()
        };

        users.push(user);
        localStorage.setItem('users', JSON.stringify(users));

        // Ghi log email ch√†o m·ª´ng (trong th·ª±c t·∫ø s·∫Ω g·ª≠i email th·∫≠t)
        console.log(`üìß [SIMULATED] G·ª≠i email ch√†o m·ª´ng ƒë·∫øn ${email}`);
        console.log(`üëã Xin ch√†o ${fullName}! Ch√†o m·ª´ng ƒë·∫øn v·ªõi Bike Shop t·ª´ Google!`);
      }

      // L∆∞u th√¥ng tin ƒëƒÉng nh·∫≠p
      localStorage.setItem('currentUser', JSON.stringify(user));
      localStorage.setItem('googleToken', token);

      // Th√¥ng b√°o th√†nh c√¥ng
      onLogin(user);
      onClose();

      alert(`üéâ Ch√†o m·ª´ng ${fullName}! ƒêƒÉng nh·∫≠p Google th√†nh c√¥ng.`);

      // N·∫øu c√≥ backend, g·ª≠i th√¥ng tin ƒë·∫øn server
      if (USE_BACKEND) {
        try {
          await fetch(`${BACKEND_URL}/auth/google`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ token })
          });
        } catch (error) {
          console.log('Backend kh√¥ng kh·∫£ d·ª•ng, s·ª≠ d·ª•ng localStorage');
        }
      }

    } catch (error) {
      console.error('Google login error:', error);
      setErrors({ general: 'ƒêƒÉng nh·∫≠p Google th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i.' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleError = () => {
    setErrors({ general: 'ƒêƒÉng nh·∫≠p v·ªõi Google th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i.' });
  };

  // ========== EMAIL/PASSWORD LOGIN ==========
  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    const formErrors = validateLogin();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    setIsLoading(true);
    setErrors({});

    try {
      // T√¨m user trong localStorage
      const users = JSON.parse(localStorage.getItem('users') || '[]');
      const user = users.find(u => u.email === loginData.email && u.password === loginData.password);

      if (user) {
        // L∆∞u th√¥ng tin ƒëƒÉng nh·∫≠p
        localStorage.setItem('currentUser', JSON.stringify(user));

        // Ghi log th√¥ng b√°o ƒëƒÉng nh·∫≠p
        console.log(`üìß [SIMULATED] G·ª≠i th√¥ng b√°o ƒëƒÉng nh·∫≠p ƒë·∫øn ${user.email}`);
        console.log(`üîí ${user.fullName} v·ª´a ƒëƒÉng nh·∫≠p v√†o t√†i kho·∫£n l√∫c ${new Date().toLocaleString('vi-VN')}`);

        // Th√¥ng b√°o th√†nh c√¥ng
        onLogin(user);
        onClose();

        alert(`üëã Ch√†o m·ª´ng ${user.fullName} quay tr·ªü l·∫°i!`);
      } else {
        setErrors({ general: 'Email ho·∫∑c m·∫≠t kh·∫©u kh√¥ng ƒë√∫ng' });
      }

      // N·∫øu c√≥ backend, ƒë·ªìng b·ªô v·ªõi server
      if (USE_BACKEND) {
        try {
          await fetch(`${BACKEND_URL}/auth/login`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(loginData)
          });
        } catch (error) {
          console.log('Backend kh√¥ng kh·∫£ d·ª•ng');
        }
      }

    } catch (error) {
      console.error('Login error:', error);
      setErrors({ general: 'ƒêƒÉng nh·∫≠p th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i.' });
    } finally {
      setIsLoading(false);
    }
  };

  // ========== REGISTRATION ==========
  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    const formErrors = validateRegister();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    setIsLoading(true);
    setErrors({});

    try {
      // Ki·ªÉm tra trong localStorage
      const users = JSON.parse(localStorage.getItem('users') || '[]');

      // Ki·ªÉm tra email ƒë√£ t·ªìn t·∫°i
      if (users.some(u => u.email === registerData.email)) {
        setErrors({ email: 'Email n√†y ƒë√£ ƒë∆∞·ª£c ƒëƒÉng k√Ω' });
        return;
      }

      // T·∫°o user m·ªõi
      const newUser = {
        id: Date.now(),
        fullName: registerData.fullName,
        email: registerData.email,
        password: registerData.password,
        emailVerified: false,
        createdAt: new Date().toISOString()
      };

      users.push(newUser);
      localStorage.setItem('users', JSON.stringify(users));

      // Ghi log c√°c email s·∫Ω g·ª≠i
      console.log(`üìß [SIMULATED] G·ª≠i email ch√†o m·ª´ng ƒë·∫øn ${registerData.email}`);
      console.log(`üëã Xin ch√†o ${registerData.fullName}! Ch√†o m·ª´ng ƒë·∫øn v·ªõi Bike Shop!`);

      console.log(`üìß [SIMULATED] G·ª≠i email x√°c nh·∫≠n ƒë·∫øn ${registerData.email}`);
      console.log(`‚úÖ Vui l√≤ng x√°c nh·∫≠n email c·ªßa b·∫°n, ${registerData.fullName}!`);

      // Hi·ªÉn th·ªã th√¥ng b√°o th√†nh c√¥ng
      setSuccessMessage('‚úÖ ƒêƒÉng k√Ω th√†nh c√¥ng! Email x√°c nh·∫≠n ƒë√£ ƒë∆∞·ª£c g·ª≠i (m√¥ ph·ªèng).');

      // T·ª± ƒë·ªông chuy·ªÉn sang tab ƒëƒÉng nh·∫≠p sau 3 gi√¢y
      setTimeout(() => {
        setActiveTab('login');
        setSuccessMessage('');
        setRegisterData({ 
          fullName: '', 
          email: '', 
          password: '', 
          confirmPassword: '' 
        });
        // T·ª± ƒë·ªông ƒëi·ªÅn email v√†o form ƒëƒÉng nh·∫≠p
        setLoginData(prev => ({ ...prev, email: registerData.email }));
      }, 3000);

      // N·∫øu c√≥ backend, ƒë·ªìng b·ªô v·ªõi server
      if (USE_BACKEND) {
        try {
          await fetch(`${BACKEND_URL}/auth/register`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              fullName: registerData.fullName,
              email: registerData.email,
              password: registerData.password
            })
          });
        } catch (error) {
          console.log('Backend kh√¥ng kh·∫£ d·ª•ng');
        }
      }

    } catch (error) {
      console.error('Register error:', error);
      setErrors({ general: 'ƒêƒÉng k√Ω th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i.' });
    } finally {
      setIsLoading(false);
    }
  };

  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleRegisterChange = (e) => {
    const { name, value } = e.target;
    setRegisterData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateLogin = () => {
    const newErrors = {};
    if (!loginData.email) newErrors.email = 'Vui l√≤ng nh·∫≠p email';
    if (!loginData.password) newErrors.password = 'Vui l√≤ng nh·∫≠p m·∫≠t kh·∫©u';
    return newErrors;
  };

  const validateRegister = () => {
    const newErrors = {};
    if (!registerData.fullName) newErrors.fullName = 'Vui l√≤ng nh·∫≠p h·ªç t√™n';
    if (!registerData.email) newErrors.email = 'Vui l√≤ng nh·∫≠p email';
    if (!registerData.password) newErrors.password = 'Vui l√≤ng nh·∫≠p m·∫≠t kh·∫©u';
    if (registerData.password.length < 6) newErrors.password = 'M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±';
    if (registerData.password !== registerData.confirmPassword) {
      newErrors.confirmPassword = 'M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp';
    }
    return newErrors;
  };

  const handleSwitchToRegister = () => {
    setActiveTab('register');
    setErrors({});
    setSuccessMessage('');
  };

  const handleSwitchToLogin = () => {
    setActiveTab('login');
    setErrors({});
    setSuccessMessage('');
  };

  return (
    <div className="auth-overlay" onClick={onClose}>
      <div className="auth-modal" onClick={(e) => e.stopPropagation()}>
        <button className="close-auth-btn" onClick={onClose}>√ó</button>

        <div className="auth-tabs">
          <button 
            className={`auth-tab ${activeTab === 'login' ? 'active' : ''}`}
            onClick={() => setActiveTab('login')}
          >
            ƒêƒÉng nh·∫≠p
          </button>
          <button 
            className={`auth-tab ${activeTab === 'register' ? 'active' : ''}`}
            onClick={() => setActiveTab('register')}
          >
            ƒêƒÉng k√Ω
          </button>
        </div>

        {GOOGLE_CLIENT_ID && (
          <div className="google-login-section">
            <GoogleLogin
              onSuccess={handleGoogleSuccess}
              onError={handleGoogleError}
              theme="filled_blue"
              size="large"
              text="signin_with"
              shape="rectangular"
              locale="vi"
              width="350"
            />
            <div className="divider">
              <span>ho·∫∑c s·ª≠ d·ª•ng email</span>
            </div>
          </div>
        )}

        {activeTab === 'login' ? (
          <form className="auth-form" onSubmit={handleLoginSubmit}>
            {errors.general && <div className="auth-error">{errors.general}</div>}

            <div>
              <input
                type="email"
                name="email"
                placeholder="Email"
                className="auth-input"
                value={loginData.email}
                onChange={handleLoginChange}
              />
              {errors.email && <div className="auth-error">{errors.email}</div>}
            </div>

            <div>
              <input
                type="password"
                name="password"
                placeholder="M·∫≠t kh·∫©u"
                className="auth-input"
                value={loginData.password}
                onChange={handleLoginChange}
              />
              {errors.password && <div className="auth-error">{errors.password}</div>}
            </div>

            <button type="submit" className="auth-btn" disabled={isLoading}>
              {isLoading ? 'ƒêang x·ª≠ l√Ω...' : 'ƒêƒÉng nh·∫≠p'}
            </button>

            <div className="auth-switch">
              Ch∆∞a c√≥ t√†i kho·∫£n? <button type="button" onClick={handleSwitchToRegister}>ƒêƒÉng k√Ω ngay</button>
            </div>
          </form>
        ) : (
          <form className="auth-form" onSubmit={handleRegisterSubmit}>
            {successMessage && <div className="auth-success">{successMessage}</div>}
            {errors.general && <div className="auth-error">{errors.general}</div>}

            <div>
              <input
                type="text"
                name="fullName"
                placeholder="H·ªç v√† t√™n"
                className="auth-input"
                value={registerData.fullName}
                onChange={handleRegisterChange}
              />
              {errors.fullName && <div className="auth-error">{errors.fullName}</div>}
            </div>

            <div>
              <input
                type="email"
                name="email"
                placeholder="Email"
                className="auth-input"
                value={registerData.email}
                onChange={handleRegisterChange}
              />
              {errors.email && <div className="auth-error">{errors.email}</div>}
            </div>

            <div>
              <input
                type="password"
                name="password"
                placeholder="M·∫≠t kh·∫©u"
                className="auth-input"
                value={registerData.password}
                onChange={handleRegisterChange}
              />
              {errors.password && <div className="auth-error">{errors.password}</div>}
            </div>

            <div>
              <input
                type="password"
                name="confirmPassword"
                placeholder="X√°c nh·∫≠n m·∫≠t kh·∫©u"
                className="auth-input"
                value={registerData.confirmPassword}
                onChange={handleRegisterChange}
              />
              {errors.confirmPassword && <div className="auth-error">{errors.confirmPassword}</div>}
            </div>

            <button type="submit" className="auth-btn" disabled={isLoading}>
              {isLoading ? 'ƒêang x·ª≠ l√Ω...' : 'ƒêƒÉng k√Ω'}
            </button>

            <div className="auth-switch">
              ƒê√£ c√≥ t√†i kho·∫£n? <button type="button" onClick={handleSwitchToLogin}>ƒêƒÉng nh·∫≠p</button>
            </div>
            <div className="debug-info">
              <small>
                üìß Email ƒë∆∞·ª£c m√¥ ph·ªèng trong Console (F12)
                {USE_BACKEND && ' | üîó ƒêang k·∫øt n·ªëi backend'}
              </small>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default Auth;
