import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaShoppingCart, FaHeart, FaBox, FaMapMarkerAlt, FaKey, FaSignOutAlt, FaCog, FaStar, FaHeadset } from 'react-icons/fa';
import './Auth.css';

function UserMenu({ user, onLogout, onShowAuth, onShowOrders, cartItemCount = 0 }) {
  const [showDropdown, setShowDropdown] = useState(false);
  const [wishlistCount, setWishlistCount] = useState(0);
  const navigate = useNavigate();

  // Lấy số lượng wishlist từ localStorage
  useEffect(() => {
    const updateWishlistCount = () => {
      const wishlist = JSON.parse(localStorage.getItem('wishlist')) || [];
      setWishlistCount(wishlist.length);
    };

    updateWishlistCount();

    // Lắng nghe sự kiện wishlist thay đổi
    window.addEventListener('wishlistUpdated', updateWishlistCount);

    return () => {
      window.removeEventListener('wishlistUpdated', updateWishlistCount);
    };
  }, []);

  const getInitials = (name) => {
    return name
      .split(' ')
      .map(word => word[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    onLogout();
    setShowDropdown(false);
  };

  const handleWishlistClick = () => {
    navigate('/wishlist');
    setShowDropdown(false);
  };

  const handleOrdersClick = () => {
    navigate('/orders');
    setShowDropdown(false);
  };

  const handleProfileClick = () => {
    navigate('/profile');
    setShowDropdown(false);
  };

  const handleCartClick = () => {
    navigate('/cart');
    setShowDropdown(false);
  };

  const handleAddressClick = () => {
    navigate('/addresses');
    setShowDropdown(false);
  };

  const handleChangePasswordClick = () => {
    navigate('/change-password');
    setShowDropdown(false);
  };

  const handleReviewsClick = () => {
    navigate('/my-reviews');
    setShowDropdown(false);
  };

  const handleSettingsClick = () => {
    navigate('/settings');
    setShowDropdown(false);
  };

  const handleSupportClick = () => {
    navigate('/support');
    setShowDropdown(false);
  };

  if (!user) {
    return (
      <div className="user-menu-not-logged">
      <button 
        className="auth-btn login-btn" 
        onClick={onShowAuth}
      >
        <FaUser className="btn-icon" />
        Đăng nhập
      </button>
      <button 
          className="auth-btn register-btn" 
          onClick={() => onShowAuth('register')}
        >
          Đăng ký
        </button>
      </div>
    );
  }

  return (
    <div className="user-menu" onMouseEnter={() => setShowDropdown(true)} onMouseLeave={() => setShowDropdown(false)}>
      <div className="user-avatar">
        {getInitials(user.fullName)}
      </div>
      <span style={{ color: '#ffffff' }}>Xin chào, {user.fullName.split(' ')[0]}</span>

      {showDropdown && (
        <div className="user-dropdown">
          <button className="dropdown-item" onClick={onShowOrders}>
            Đơn hàng của tôi
          </button>
          <button className="dropdown-item">
            Wishlist
          </button>
          <button className="dropdown-item">
            Thông tin tài khoản
          </button>
          <div className="dropdown-divider"></div>
          <button className="dropdown-item" onClick={handleLogout}>
            Đăng xuất
          </button>
        </div>
      )}
    </div>
  );
}

export default UserMenu;
